  var routeController = {
    init : function(wpCurrent,wpTotal,tripName,tripStarted,tripEnded,tripExportDate,tripTotalDistance,distanceUnit,tripAverageSpeed,speedUnit,tripMaxAltitude,maxAltUnit,tripMinAltitude,minAltUnit,tripElapsedTimeDays,tripElapsedTimeHours,tripElapsedTimeMin,tripElapsedTimeSec,system,smallDistance) {
      wpScroller.currIdx        = wpCurrent;
      wpScroller.totalImg       = wpTotal;
      this.tripName				      = tripName;
      this.tripStarted          = tripStarted;
      this.tripEnded            = tripEnded;
      this.tripExportDate       = tripExportDate;
      this.tripTotalDistance    = tripTotalDistance;
      this.tripAverageSpeed     = tripAverageSpeed;
      this.tripMaxAltitude      = tripMaxAltitude;
      this.tripMinAltitude      = tripMinAltitude;
      this.tripElapsedTimeDays  = tripElapsedTimeDays;
      this.tripElapsedTimeHours = tripElapsedTimeHours;
      this.tripElapsedTimeMin   = tripElapsedTimeMin;
      this.tripElapsedTimeSec   = tripElapsedTimeSec;
      this.distanceMeasure      = distanceUnit;
      this.speedMeasure         = speedUnit;
      this.maxAltitudeMeasure   = maxAltUnit;
      this.minAltitudeMeasure   = minAltUnit;
    },

    set : function() {
      wpElement.text("set","TripName",this.tripName);
      wpElement.text("set","TripStarted",this.tripStarted);
      if ( this.tripEnded != "" ) { wpElement.text("set","TripEnded",this.tripEnded); }
      wpElement.text("set","TripExportDate",this.tripExportDate);
      if ( this.tripTotalDistance != "" ) { 
        wpElement.text("set","TotalDistanceValue",this.tripTotalDistance);
        wpElement.text("set","TotalDistanceUnit",this.distanceMeasure);
      }
      if ( this.tripAverageSpeed != "" ) { 
        wpElement.text("set","AverageSpeedValue",this.tripAverageSpeed);
        wpElement.text("set","AverageSpeedUnit",this.speedMeasure);
      }
      if ( this.tripMaxAltitude != "" ) { 
        wpElement.text("set","MaxAltitudeValue",this.tripMaxAltitude);
        wpElement.text("set","MaxAltitudeUnit",this.maxAltitudeMeasure);
      }
      if ( this.tripMinAltitude != "" ) { 
        wpElement.text("set","MinAltitudeValue",this.tripMinAltitude);
        wpElement.text("set","MinAltitudeUnit",this.minAltitudeMeasure);
      }
      if ( this.tripElapsedTimeDays != "" ) { 
        wpElement.style("set","ElapsedTimeDays","display","inline");
        wpElement.text("set","ElapsedTimeDaysValue",this.tripElapsedTimeDays);
      }
      if ( this.tripElapsedTimeHours == "" || this.tripElapsedTimeMin == "" ) {
        wpElement.text("set","ElapsedTimeMinUnit","minutes");
        wpElement.text("set","ElapsedTimeSecUnit","seconds");
      }
      if ( this.tripElapsedTimeHours == "" ) { wpElement.text("set","ElapsedTimeHoursUnit",""); }
      if ( this.tripElapsedTimeMin == "" ) { wpElement.text("set","ElapsedTimeMinUnit",""); }
      wpElement.text("set","ElapsedTimeHoursValue",this.tripElapsedTimeHours);
      wpElement.text("set","ElapsedTimeMinValue",this.tripElapsedTimeMin);
      wpElement.text("set","ElapsedTimeSecValue",this.tripElapsedTimeSec);

      wpScroller.set();
    }
  }

  var wpScroller = {
    totalImg        : 1,
    howManyImg      : 5,
    currIdx         : 1,
    isScrollActive  : true,

    set : function() {
      if ( this.totalImg &lt;= this.howManyImg ) {
        this.howManyImg = this.totalImg ;
        this.isScrollActive = false ;
      }
      wpElement.style("set","cur" + this.currIdx,"visibility","visible");
      if ( !this.isScrollActive ) {
        wpElement.style("set","prevArrow","cursor","default");
        wpElement.style("set","nextArrow","cursor","default");
      }    
    },

    show : function() {
      for ( var i = 0 ; i &lt; this.totalImg ; i++ ) {
        j = this.currIdx + i ;
        if ( j &gt; this.totalImg ) { j = j % this.totalImg ; }
        if ( j &lt;= 0 ) { j = this.totalImg + this.currIdx ; }
        if ( i &lt; this.howManyImg ) {
          wpElement.style("set","pic" + j,"display","block");
          wpElement.style("set","pic" + j,"left",( i * divWidth ) + "px");
        } else {
          wpElement.style("set","pic" + j,"display","none");
        }
      }
    },

    change : function(currImg) {
      wpElement.attrib("take","wpCurrImg","src","","thumb"+currImg,"src");
      if( parseInt(document.getElementById("thumb"+currImg).clientHeight) &lt;= 1 ) {
        wpElement.attrib("set","wpCurrImg","class","imgHidden");
      } else {
        wpElement.attrib("set","wpCurrImg","class","imgVisible");
        if ( parseInt(document.getElementById("thumb"+currImg).clientHeight) &gt; parseInt(document.getElementById("thumb"+currImg).clientWidth) ) {
          wpElement.attrib("add","wpCurrImg","class"," imgPortrait");
        } else {
          wpElement.attrib("add","wpCurrImg","class"," imgLandscape");
        }
      }
      for ( var i = 1 ; i &lt;= this.totalImg ; i++ ) {
        wpElement.style("set","cur" + i,"visibility","hidden") ;
      }
      wpElement.style("set","cur" + currImg,"visibility","visible");
	  wpElement.attrib("take","wpLink","href","","link" + currImg, "href");
	  wpElement.text("set","currWP",wpElement.attrib("get","thumb"+currImg,"alt").substr(0,subCurrent));
	  wpElement.attrib("take","currWP","title","","thumb"+currImg, "alt");
    },

    next : function() {
      this.currIdx++;
      if ( this.currIdx &gt; this.totalImg ) { this.currIdx = this.currIdx % this.totalImg ; }
    },

    prev : function() {
      this.currIdx--;
      if ( this.currIdx &lt; 0 ) { this.currIdx = this.totalImg + this.currIdx ; }
    }
  }

  var wpElement = {
    attrib : function(action,objID,attr,val,objID2,attr2) {
      obj = document.getElementById(objID);
      obj2 = document.getElementById(objID2);
      switch (action) {
        case "get": return obj.attributes[attr].nodeValue; break;
        case "set": obj.attributes[attr].nodeValue = val; break;
        case "add": obj.attributes[attr].nodeValue += val; break;
        case "take": obj.attributes[attr].nodeValue = obj2.attributes[attr2].nodeValue; break;
      }
    },

    text : function(action,objID,content) {
      obj = document.getElementById(objID);
      switch (action) {
        case "get": return obj; break;
        case "set": obj.innerText = content; break;
        case "add": obj.innerText += content; break;
      }
    },

    style : function(action,objID,styleName,styleVal) {
      obj = document.getElementById(objID);
      switch (action) {
        case "get" : return obj.style[styleName]; break;
        case "set" : obj.style[styleName] = styleVal; break;
      }
    }
  }
