  var wpController = {    
    init : function(wpCurrent,wpTotal,wpPictures,wpTitle,wpCreated,wpDate,wpTime,wpLat,wpLng,wpAlt,wpElapsed,wpGeoCode1,wpGeoCode2,wpComments) {
      wpScroller.currWayPoint = wpCurrent;
      wpScroller.totalImg     = wpTotal;
      wpScroller.totalPic     = wpPictures;
      this.titleWayPoint    = wpTitle;
      this.createdWayPoint  = wpCreated;
      this.dateWayPoint     = wpDate;
      this.timeWayPoint     = wpTime;
      this.latWayPoint      = wpLat;
      this.lngWayPoint      = wpLng;
      this.altWayPoint      = wpAlt;
      this.elapsedTime      = wpElapsed;
      this.geoCode1      		= wpGeoCode1;
      this.geoCode2      		= wpGeoCode2;
      wpComment.set(wpComments);
    },

    set : function() {
      wpScroller.set();
      wpNote.rotate() ;
      wpElement.setText("wpTitle",this.titleWayPoint) ;
      wpElement.setText("wpCreated",this.createdWayPoint) ;
      wpElement.setText("wpDate",this.dateWayPoint) ;
      wpElement.setText("wpTime",this.timeWayPoint) ;
      if ( this.latWayPoint != "" ) { wpElement.setText("wpLat",this.latWayPoint) ; }
      if ( this.lngWayPoint != "" ) { wpElement.setText("wpLng",this.lngWayPoint) ; }
      if ( this.altWayPoint != "" ) { wpElement.setText("wpAlt",this.altWayPoint) ; }
      if ( this.elapsedTime != "" ) { wpElement.setInner("elapsedTime",this.elapsedTime.replace(",",",&lt;br /&gt;")) ; }
      if ( this.geoCode1 != "" ) { wpElement.setText("geoCodePart1",this.geoCode1) ; }
      if ( this.geoCode2 != "" ) { wpElement.setText("geoCodePart2",this.geoCode2) ; }
      wpElement.setBoxBg("wpImages","bgBoxesRight");
      wpElement.setBoxBg("wpPosition","bgBoxesRight");
      wpElement.setBoxBg("wpGeoCodes","bgBoxesRight");
      wpElement.setBoxBg("wpCommentContainer","bgBoxesRight");
      wpComment.rotate();
    }
