  var wpScroller = {
    totalImg        : 1,
    howManyImg      : 5,
    currWayPoint    : 1,
    currIdx         : 2,
    totalPic        : 1,
    isScrollActive  : true,

    set : function() {
      if ( this.totalImg &lt;= this.howManyImg ) {
        this.howManyImg = this.totalImg ;
        this.isScrollActive = false ;
      }
      if ( !this.isScrollActive ) {
        wpElement.setStyle("prevArrow","cursor","default") ;
        wpElement.setStyle("nextArrow","cursor","default") ;
        wpElement.setStyle("currWP","cursor","default") ;
      }
	  wpElement.setAttrib("link"+this.currIdx,"href","wpLink","href");
    },

    show : function() {
      for ( var i = 0 ; i &lt; this.totalImg ; i++ ) {
        j = this.currIdx + i ;
        if ( j &gt; this.totalImg ) { j = j % this.totalImg ; }
        if ( j &lt;= 0 ) { j =  this.totalImg + this.currIdx ; }
        if ( i &lt; this.howManyImg ) {
          wpElement.setStyle("pic" + j,"display","block") ;
          wpElement.setStyle("pic" + j,"left",( i * divWidth ) + "px") ;
        } else {
          wpElement.setStyle("pic" + j,"display","none") ;
        }
      }
    },

    change : function(currImg) {
      wpElement.setStyle("wpCurrContainer","display","block") ;
      wpElement.setSrc("wpCurrImg","thumb"+currImg) ;
      if( parseInt(document.getElementById("thumb"+currImg).clientHeight) &lt;= 1 ) {
        document.getElementById("wpCurrImg").className = "imgHidden";
      } else {
        document.getElementById("wpCurrImg").className = "imgVisible";
        if ( parseInt(document.getElementById("thumb"+currImg).clientHeight) &gt; parseInt(document.getElementById("thumb"+currImg).clientWidth) ) {
          document.getElementById("wpCurrImg").className += " imgPortrait" ;
        } else {
          document.getElementById("wpCurrImg").className += " imgLandscape" ;
        }
      }
      for ( var i = 1 ; i &lt;= this.totalImg ; i++ ) {
        wpElement.setStyle("cur" + i,"visibility","hidden") ;
      }
      wpElement.setStyle("cur" + currImg,"visibility","visible") ;
	  wpElement.setSrc("wpLink","link"+currImg);
	  wpElement.setInner("currWP",document.getElementById("thumb"+currImg).alt.substr(0,subCurrent));
	  wpElement.setAttrib("currWP","title","thumb"+currImg,"alt");
	  wpElement.setAttrib("wpLink","href","link"+currImg,"href");
    },

    next : function() {
      this.currIdx++;
      if ( this.currIdx &gt; this.totalImg ) { this.currIdx = this.currIdx % this.totalImg ; }
    },

    prev : function() {
      this.currIdx--;
      if ( this.currIdx &lt; 0 ) { this.currIdx = this.totalImg + this.currIdx ; }
    }
  }

  var wpComment = {
    currComment     : 0,
    commentWayPoint : '',
    isFirstComment  : true,
    isLastComment   : false,

    set : function(comment) {
      if ( comment == "" ) {
		comment = "There are no notes for this waypoint";
      }
      this.commentWayPoint = comment.split("$@^%!*") ;
    },

    rotate : function(type) {
      if ( type == "P" ) { this.currComment--; }
      if ( type == "N" ) { this.currComment++; }
      pos = 0 ;
      wpElement.setStyle("wpComments","top","0") ;
      wpElement.setStyle("group","display","block") ;
      wpElement.setStyle("wpCommentUp","display","none") ;
      wpElement.setStyle("wpCommentDown","display","none") ;
      wpElement.setText("wpComments",this.commentWayPoint[this.currComment]) ;
      if (this.commentWayPoint[this.currComment].length &gt; subComment ) {
        wpElement.setText("wpComments",this.commentWayPoint[this.currComment].substr(0,subComment)) ;
        wpElement.addInner("wpComments",' &lt;span id="wpChangeComment" onclick="wpComment.change()"&gt; [...]&lt;/span&gt;') ;
      }
      wpElement.setStyle("wpCommentContent","height",document.getElementById("wpComments").clientHeight) ;
      if ( this.currComment == 0 ) { this.isFirstComment = true; wpElement.setSrc("prevComment","prevDisabled"); } else { this.isFirstComment = false; wpElement.setSrc("prevComment","prevActive"); }
      if ( this.currComment == (this.commentWayPoint.length - 1) ) { this.isLastComment = true; wpElement.setSrc("nextComment","nextDisabled"); } else { this.isLastComment = false; wpElement.setSrc("nextComment","nextActive"); }
    },

    change : function() {
      pos = 0 ;
      wpElement.setStyle("wpComments","top","0") ;
      wpElement.setStyle("wpCommentUp","display","none") ;
      wpElement.setStyle("wpCommentDown","display","none") ;
      if ( document.getElementById("group").style.display != "none" ) {
        wpElement.setStyle("group","display","none") ;
        wpElement.setText("wpComments",this.commentWayPoint[this.currComment]);
		wpElement.addInner("wpComments",' &lt;span id="wpChangeComment" onclick="wpComment.change()"&gt; [close]&lt;/span&gt;') ;
        wpElement.setStyle("wpCommentContent","height",document.getElementById("wpComments").clientHeight) ;
        if ( document.getElementById("wpComments").clientHeight &gt; maxCommentHeight ) {
          wpElement.setStyle("wpCommentUp","display","block") ;
          wpElement.setStyle("wpCommentDown","display","block") ;
          wpElement.setStyle("wpCommentContent","height",maxCommentHeight) ;
        }
      } else {
        wpElement.setStyle("group","display","block") ;
        wpElement.setText("wpComments",this.commentWayPoint[this.currComment].substr(0,subComment)) ;
        wpElement.addInner("wpComments",'&lt;span id="wpChangeComment" onclick="wpComment.change()"&gt; [...]&lt;/span&gt;') ;
        wpElement.setStyle("wpCommentContent","height",document.getElementById("wpComments").clientHeight) ;
      }
    }
  }

  var wpNote = {
    currPic         : 1,
    isFirstPic      : true,
    isLastPic       : false,

    rotate : function(type) {
      if ( type == "P" ) { this.currPic--; }
      if ( type == "N" ) { this.currPic++; }
	  isNoteExpanded = false ;
	  maxNoteHeight = maxNoteHeightP;
      wpElement.setStyle("leftSide","top","0px");
      wpElement.setStyle("wpVideo","display","none");
      wpElement.setSrc("wpImg","wpPic"+this.currPic) ;
      document.getElementById("wpImg").className = document.getElementById("wpPic"+this.currPic).className ;
      switch (document.getElementById("wpImg").className) {
        case "picLandscape":
          wpElement.setBoxBg("wpImgContainer","wpImgFrameLandscape");
		  wpElement.setStyle("leftSide","top","100px");
		  maxNoteHeight = maxNoteHeightL;
          break;
        case "picPortrait":
          wpElement.setBoxBg("wpImgContainer","wpImgFramePortrait");
          break;
        case "picHidden":
          wpElement.setBoxBg("wpImgContainer","wpImgFramePortrait");
          break;
      }
      if (document.getElementById("wpPic"+this.currPic).alt.length &lt; subNote ) {
        wpElement.setInner("picNoteContent",document.getElementById("wpPic"+this.currPic).alt + "&amp;nbsp;") ;
      } else {
        wpNote.change();
      }
	  wpElement.setBoxBg("wpPicNote","picNoteBgLongTop");
      wpNote.release() ;
	  if ( document.getElementById("wpPic"+this.currPic).title.length &gt; subPhoto ) {
        wpElement.setText("currPhoto",document.getElementById("wpPic"+this.currPic).title.substr(0,subPhoto)) ;
        wpElement.addInner("currPhoto",' ...') ;
      } else {
        wpElement.setText("currPhoto",document.getElementById("wpPic"+this.currPic).title) ;
        wpElement.addInner("currPhoto",'&amp;nbsp;') ;
      }
      wpElement.setAttrib("currPhoto","title","wpPic"+this.currPic,"title") ;
      if ( this.currPic == 1 ) { this.isFirstPic = true; wpElement.setSrc("prevPic","prevDisabled"); } else { this.isFirstPic = false; wpElement.setSrc("prevPic","prevActive"); }
      if ( this.currPic == wpScroller.totalPic ) { this.isLastPic = true; wpElement.setSrc("nextPic","nextDisabled"); } else { this.isLastPic = false; wpElement.setSrc("nextPic","nextActive"); }
    },

    change : function() {
      wpNote.release() ;
      if ( !isNoteExpanded ) {
        wpElement.setInner("picNoteContent",document.getElementById("wpPic"+this.currPic).alt.substr(0,subNote)) ;
        wpElement.addInner("picNoteContent",'&lt;span id="wpChangeNote" onclick="wpNote.change()"&gt; [expand]&lt;/span&gt;') ;
				isNoteExpanded = true ;
        wpElement.setStyle("wpPicNote","height",document.getElementById("picNoteContent").clientHeight) ;
      } else {
        wpElement.setInner("picNoteContent",document.getElementById("wpPic"+this.currPic).alt) ;
        wpElement.addInner("picNoteContent",'&lt;center&gt;&lt;div id="wpChangeNote" onclick="wpNote.change()"&gt; collapse [x]&lt;/div&gt;&lt;/center&gt;') ;
        wpElement.setStyle("wpPicNote","z-index","+1") ;
        wpElement.setStyle("wpVideo","display","none");
				isNoteExpanded = false ;
        wpElement.setStyle("wpPicNote","height",document.getElementById("picNoteContent").clientHeight) ;
        wpElement.setStyle("picNoteBgBottom","display","block") ;
        wpElement.setStyle("picNote","height",document.getElementById("wpPicNote").clientHeight-17) ;
        if ( document.getElementById("picNoteContent").clientHeight &gt; maxNoteHeight ) {
          wpElement.setStyle("wpPicNote","height",maxNoteHeight+"px") ;
					wpElement.setStyle("wpPicNote","paddingTop","25") ;
          wpElement.setStyle("picNoteUp","display","block") ;
          wpElement.setStyle("picNoteDown","display","block") ;
          wpElement.setStyle("picNote","height",document.getElementById("wpPicNote").clientHeight-47) ;
        }
      }
    },

    release : function() {
      wpElement.setStyle("wpPicNote","z-index","0") ;
      wpElement.setStyle("wpPicNote","paddingTop","10") ;
      wpElement.setStyle("picNote","height",document.getElementById("picNoteContent").clientHeight) ;
      wpElement.setStyle("wpPicNote","height",document.getElementById("picNote").clientHeight) ;
      wpElement.setStyle("picNoteContent","top","0") ;
      pos = 0 ;
      wpElement.setStyle("picNoteUp","display","none") ;
      wpElement.setStyle("picNoteDown","display","none") ;
      wpElement.setStyle("picNoteBgBottom","display","none") ;
      if ( document.getElementById("wpPic"+this.currPic).attributes['longdesc'].nodeValue != "" ) {
        wpElement.setAttrib("wpMovie","src","wpPic"+this.currPic,"longdesc"); 
        wpElement.setClass("wpVideoObj","wpPic"+this.currPic) ;
        wpElement.setStyle("wpVideo","display","block");
      }
    }
  }

  var wpElement = {
    setAttrib : function(objID1,attr1,objID2,attr2) {
      document.getElementById(objID1).attributes[attr1].nodeValue = document.getElementById(objID2).attributes[attr2].nodeValue ;
    },

    setBoxBg : function(objID,imgID) {
      wpElement.setStyle(objID,"backgroundImage","url(" + document.getElementById(imgID).src + ")") ;
      wpElement.setStyle(objID,"backgroundPosition","top center") ;
      wpElement.setStyle(objID,"backgroundRepeat","repeat-y") ;
    },

    setClass : function(objID1,objID2) {
      document.getElementById(objID1).className = document.getElementById(objID2).className;
    },

    setText : function(objID,content) {
      document.getElementById(objID).innerText = content ;
    },

    setInner : function(objID,content) {
      document.getElementById(objID).innerHTML = content ;
    },

    addInner : function(objID,content) {
      document.getElementById(objID).innerHTML += content ;
    },

    scrollUp : function(objID) {
	  if ( pos &lt; 0 ) {
        pos += step ;
        wpElement.setStyle(objID,"top",pos);
	  }
    },

    scrollDown : function(objID) {
      if ( pos &gt;= - (document.getElementById(objID).clientHeight - 30) ) {
	    pos -= step ;
	    wpElement.setStyle(objID,"top",pos);
	  }
    },

    setSrc : function(objID1,objID2) {
      document.getElementById(objID1).src = document.getElementById(objID2).src;
    },

    setStyle : function(objID,styleName,styleVal) {
      document.getElementById(objID).style[styleName] = styleVal ;
    }
  }
