  #leftSide         {width:310px; margin-left:10px; margin-right:12px; height:100%; float:left; position:relative; text-align:center;}
  #wpPicNote        {position:absolute; width:310px; top:16px; padding-top:10px; padding-bottom:5px;}
  #picNoteBgBottom  {position:absolute; bottom:-12px; left:20px; display:none;}
  #picNote          {position:absolute; width:310px; overflow:hidden;}
  #picNoteContent   {position:absolute; width:240px; left:35px; text-align:justify; overflow:hidden;}
  #wpImgContainer   {position:relative; margin-top:46px; width:auto;}
  #wpVideo          {position:absolute; top:0px; left:0px; text-align:center; display:none; width:100%;}
  #wpPicNav         {position:relative; width:265px; padding-top:8px; margin-top:-2px;}
  #currPhoto        {float:left; width:200px; color:#3d584c; text-align:center; font-weight:bold; font-size:12px; font-family:Georgia;}
  .arrows2          {float:left; cursor:pointer; margin-left:13px; height:11px; margin-top:2px;}
  #rightSide        {width:299px; margin-left:16px; margin-right:17px; height:100%; float:left; position:relative; overflow:hidden; text-align:center;}
  #wpTitle          {font-size:12px; margin-top:12px; font-weight:bold; color:#ddd5bd;}
  #wpCreated        {font-size:11px; margin-top:4px; margin-bottom:15px; font-family:Georgia; color:#797051;}
  #wpCurrContainer  {width:91px; height:91px; float:left; position:relative; margin:5px;}
  #imgBar           {height:44px; position:relative; text-align:left; margin-top:20px;}
  #currWP           {float:left; width:125px; height:33px; cursor:pointer; color:#3d584c; font-size:9px; text-align:center; display:block;}
  .arrows           {float:left; cursor:pointer; width:31px; height:9px; margin-top:3px;}
  #descript         {float:left; width:69%; overflow:hidden; white-space:nowrap; font-weight:bold; font-size:11px; text-align:left;}
