  #geoCodePart2			{float:right; text-align:right; padding-right:5px;}
  #wpCommentContainer {width:299px; position:relative;}
  #wpCommentContent {float:left; position:relative; overflow:hidden; width:260px;}
  #wpComments        {position:absolute; top:0px; width:260px; font-size:11px; text-align:justify;}
  .arrows3          {float:left; cursor:pointer; margin:3px; height:11px;}
