  .imgVisible       {display:block;}
  .picContainer     {position:absolute; width:34px; height:44px; display:none; text-align:center; margin:0;}
  .picFrame         {z-index:-1; position:absolute; top:9px; left:0px;}
  .thumbPortrait    {height:45px; width:30px; margin-top:-5px; margin-left:0px;}
  .thumbLandscape   {height:30px; width:45px; margin-top:0px; margin-left:-5px;}
  .thumbHidden      {visibility:hidden;}
  .thumbContainer   {width:30px; height:30px; margin:2px; margin-top:6px; overflow:hidden;}
